import java.util.*;

public class rec {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s = new Scanner(System.in);
		System.out.println("한 변의 길이를 정해주세요.");
		int number;
		number = s.nextInt();

		
		for (int a = 1; a <= number; a++) {

			for (int b = 1; b <= number; b++) {
				System.out.print("*");
		}
	System.out.println();}
	}

}
